<!DOCTYPE html><html lang="en">	<?php		$color = $_GET["color"];		$textcolor = $_GET["color_text"];		$type = $_GET["type"];		$method = $_GET["method"];				if ($color == "0") {			$color_param = "black";		}		if ($color == "1") {			$color_param = "white";		}		if ($color == "2") {			$color_param = "aqua";		}		if ($textcolor == "0") {			$textcolor_param = "black";		}		if ($textcolor == "1") {			$textcolor_param = "white";		}		if ($textcolor == "2") {			$textcolor_param = "aqua";		}	?>	<head>		<meta charset="utf-8">		<style>			p {			margin-top: 0.5em;			margin-bottom: 1em;			}			.svg1 {			transform: rotate(-90deg);			}			.ground{			background-color: <?= $color_param ?>;			}		</style>	</head>	<body class="ground">		<p>		<?php		if($type == "graph" && $method == "canvas"){		?>			<h2>graph + canvas</h2>			<canvas id="graph_canvas"></canvas>		<?php		}		?>		</p>		<p>		<?php		if($type == "text" && $method == "canvas"){		?>			<h2>text + canvas</h2>			<canvas height='100' width='300' id='text_canvas' ></canvas>		<?php		}		?>		</p>		<p>		<?php		if($type == "graph" && $method == "svg"){		?>		<h2>graph + svg</h2>		<svg>		<path d="M50 95 L62 25 L75 95 M55 60 L70 60" stroke="<?= $textcolor_param ?>" fill="<?= $color_param ?>"/>		<path d="M100 25 L130 95 M100 95 L130 25" stroke="<?= $textcolor_param ?>"/>        <path d="M150 25 L150 95 M150 25 L180 25" stroke="<?= $textcolor_param ?>"/>		</svg>		<?php		}		?>		</p>		<p>			<?php			if($type == "text" && $method == "svg"){			?>				<h2>text + svg</h2>					<svg height="100" width="300">						<text x="20" y="100" font-size="40" fill="<?= $textcolor_param ?>">A. Х. Г.</text>					</svg>			<?php			}			?>		</p>		<script>			  var canvas = document.getElementById('graph_canvas');			  var subCanvas = canvas.getContext('2d')			  subCanvas.beginPath();			  subCanvas.fillStyle =  '<?= $color_param ?>';			  subCanvas.fillRect(0, 0, 400, 400)			  subCanvas.strokeStyle = '<?= $textcolor_param ?>';			  subCanvas.moveTo(30, 30)			  subCanvas.lineTo(14, 100)			  subCanvas.stroke()			  subCanvas.moveTo(22, 65)			  subCanvas.lineTo(38, 65)			  subCanvas.stroke()			  subCanvas.moveTo(30, 30)			  subCanvas.lineTo(46, 100)			  subCanvas.stroke()			  subCanvas.moveTo(50, 30)			  subCanvas.lineTo(80, 100)			  subCanvas.stroke()			  subCanvas.moveTo(80, 30)			  subCanvas.lineTo(50, 100)			  subCanvas.stroke()			  subCanvas.moveTo(100, 30)			  subCanvas.lineTo(130, 30)			  subCanvas.stroke();			  subCanvas.moveTo(100, 30);			  subCanvas.lineTo(100, 100);			  subCanvas.stroke();		</script>		<script>			  var canvas = document.getElementById('text_canvas')			  var subCanvas = canvas.getContext('2d')			  var texx = "A. X. Г."			  subCanvas.font = "20pt Comic Sans MS"			  subCanvas.fillStyle = '<?= $textcolor_param ?>';			  subCanvas.fillText(texx, 10, 75)		</script>	</body></html>